"use strict";var setDarkMode=function(){document.querySelector("body").classList="dark"},setLightMode=function(){document.querySelector("body").classList="light"},setColorMode=function(){"dark"==localStorage.getItem("colorMode")?(setDarkMode(),document.getElementById("dark").checked=!0):(setLightMode(),document.getElementById("dark").checked=!1)};setColorMode();for(var radioButton=document.querySelectorAll(".toggle__dark"),i=0;i<radioButton.length;i++)radioButton[i].addEventListener("click",(function(e){document.getElementById("dark").checked?(localStorage.setItem("colorMode","light"),setLightMode()):(localStorage.setItem("colorMode","dark"),setDarkMode())}));